<template>
    <div class="card">
        <div class="card-header">
            <h1 class="card-title">App authorization</h1>
        </div>
        <div class="card-body">
            <template v-if="!checking">
                <template v-if="!auth">
                    <p>Press the Authorize button to authorize the twitch app<br/>(it's 100% safe, we don't store your data)</p>
                    <p><a :href="authURL" class="btn btn-danger">Authorize</a></p><hr>
                    <p>If you already have a valid authorization token, please use it here:<br/><input v-model="manualAuth" placeholder="Insert authorization code"/> <a class="btn btn-danger" @click.prevent="validateAuth(manualAuth)">Manual Authorization</a></p>
                    <template v-if="invalid"><p>C칩digo de autorizaci칩n inv치lido se requiere un nuevo c칩digo</p></template>
                </template>
                <template v-else>
                    <template>
                        <p>You successfuly authorized the app</p>
                        <p>Your Auth token is {{auth}} <a @click.prevent="copyToClipboard(auth)" class="btn btn-success">Copy</a></p>
                        <p v-if="timeLeft">This auth token will work during the next {{timeLeft | dateFormat}}</p>
                        <p v-if="isAboutToExpire">Your Auth Token is about to expire, please renew it <a @click.prevent="renewAuth" target="_blank" class="btn btn-warning">Renew authorization</a></p>
                    </template>
                    <p><em>If you have any login error while displaying the subs list, press this button:</em></p>
                    <p><a @click="warningClearData" class="btn btn-danger">Clear stored Authorization data</a></p>
                </template>
            </template>
            <template v-else>
                <p>Checking your authorization<br/><img height="30" width="30" src="data:image/svg+xml;base64," /></p>
            </template>
        </div>
    </div>
</template>
<script>
export default {
    name: 'AuthorizeView',
    data(){
        return{
            twitchAuthURL: 'oauth2/authorize',
            twitchValidateURL: 'oauth2/validate',
            auth:null,
            manualAuth:null,
            timeLeft:null,
            invalid:null,
            checking:true,
        }
    },
    async created(){
      //If user have a stored auth we try to validate it
      if(this.currentAuth) {
        this.validateAuth(this.currentAuth)
      }else{
        const uri = window.location.hash.substr(1)
        const params = new URLSearchParams(uri)
        const token = params.get("access_token")
        if(token){
            this.validateAuth(token)
        }else{
            this.checking=false
        }
      }
    },
    computed:{
        authURL(){
            return `${process.env.VUE_APP_AUTH_SERVER}${this.twitchAuthURL}?response_type=token&client_id=${process.env.VUE_APP_CID}&redirect_uri=${process.env.VUE_APP_SERVER_URL}auth&scope=channel:read:subscriptions`
        },
        currentAuth(){
            return localStorage.twSubsAuth
        },
        currentLogin(){
            return localStorage.twSubsLogin
        },
        isAboutToExpire(){
            if(this.timeLeft <= 604800){
                return true
            }else{
                return false
            }
        }
    },
    watch:{
        auth(newAuth) {
            if(newAuth){
                localStorage.twSubsAuth = newAuth
            }
        }
    },
    methods:{
        renewAuth(){
            this.clearAuth()
            window.location.href = this.authURL
        },
        warningClearData(){
            let confirmation = confirm("Are you sure you wanna clear your Authorization information?");
            if (confirmation) {
                this.clearAuth()
            }
        },
        clearAuth(){
            this.auth=null
            this.invalid=null
            this.timeLeft=null
            if(this.currentAuth){
                localStorage.removeItem("twSubsAuth")
                localStorage.removeItem("twSubsVerification")
            }
            if(this.currentLogin){
                localStorage.removeItem("twSubsLogin")
            }
        },
        async validateAuth(latestAuth){
            this.checking=true
            const twitchValidateURL = `${process.env.VUE_APP_AUTH_SERVER}${this.twitchValidateURL}`
            try {
                const response = await this.axios.get(twitchValidateURL, {headers:{'Authorization': `Bearer ${latestAuth}`}})
                const resp_data = response.data
                localStorage.twSubsLogin = resp_data.login
                this.timeLeft = resp_data.expires_in
                this.auth = latestAuth
                this.defaultConfig()
                this.checking=false
                this.invalid=null
            } catch (error) {
                this.invalid=error.message
                this.checking=false
                this.$notify({type:'error',text: 'Auth code invalid!'})
            }
        },
        defaultConfig(){
            if(!localStorage.twSubsStyle){
                localStorage.twSubsPagAmount = 100
                localStorage.twSubsStyle = 'default'
                localStorage.twSubsIcons = true
                localStorage.twSubsTime = 0
            }
        }
    }
}
</script>

